query GetMarketData($id: String!, $currencyCode: CurrencyCode, $countryCode: String!, $marketName: String) {
  product(id: $id) {
  brand
  styleId
  gender
  id
    urlKey
     listingType
    title
    uuid
    contentGroup
    market(currencyCode: $currencyCode) {
   deadStock
 {
 sold
 }
  bidAskData(country: $countryCode, market: $marketName) {
  highestBid
 highestBidSize
 lowestAsk
 lowestAskSize
 }
     state(country: $countryCode, market: $marketName) {
        lowestAsk {
          amount
        }
       highestBid {
          amount
        }
        numberOfCustodialAsks
        numberOfAsks
        numberOfBids
        lowestCustodialAsk {
          amount
        }
      }
    }
    variants {
   id
   pricingGuidance(currencyCode: $currencyCode, country: $countryCode, market: $marketName) {      sellingGuidance {        earnMore        }    }   market(currencyCode: $currencyCode) {
     deadStock
 {
 sold
 }
    bidAskData(country: $countryCode, market: $marketName) {
 highestBid
 highestBidSize
 lowestAsk
 lowestAskSize
 }
      state(country: $countryCode, market: $marketName) {
          lowestAsk {
            amount
          }
          highestBid {
            amount
          }
          numberOfCustodialAsks
          numberOfAsks
          numberOfBids
          lowestCustodialAsk {
            amount
          }
        }
     salesInformation {
          lastSale
          salesLast72Hours
        }
     }
    }
    ...BidButtonFragment
    ...BidButtonContentFragment
    ...BuySellFragment
    ...BuySellContentFragment
    ...XpressAskPDPFragment
    ...LastSaleFragment
  }
}

fragment BidButtonFragment on Product {
  id
  title
  urlKey
  sizeDescriptor
  productCategory
  market(currencyCode: $currencyCode) {
    state(country: $countryCode, market: $marketName) {
      highestBid {
        amount
      }
      lowestAsk {
        amount
      }
    }
  }
  media {
    imageUrl
  }
  variants {
    id
    market(currencyCode: $currencyCode) {
      state(country: $countryCode, market: $marketName) {
        highestBid {
          amount
        }
        lowestAsk {
          amount
        }
      }
    }
  }
}

fragment BidButtonContentFragment on Product {
  id
  urlKey
  sizeDescriptor
  productCategory
  lockBuying
  lockSelling
  minimumBid(currencyCode: $currencyCode)
  market(currencyCode: $currencyCode) {
    state(country: $countryCode, market: $marketName) {
      highestBid {
        amount
      }
      lowestAsk {
        amount
      }
      numberOfAsks
    }
  }
  variants {
    id
    market(currencyCode: $currencyCode) {
      state(country: $countryCode, market: $marketName) {
        highestBid {
          amount
        }
        lowestAsk {
          amount
        }
        numberOfAsks
      }
    }
  }
}

